class MaterialRetiradoBlocIR with Validators implements BlocBase { 
  final listenConection = ConnectionStatusSingleton.getInstance();
  final _materialretiradoListController = BehaviorSubject<MaterialRetiradoModelIR>();  final _idController = BehaviorSubject<int>();
  final _idRemotoController = BehaviorSubject<int>();
  final _nombreController = BehaviorSubject<String>;  final _estadoController = BehaviorSubject<int>;  final _idController = BehaviorSubject<int>;  final _idRemotoController = BehaviorSubject<int>;  final _imagenesSyncController = BehaviorSubject<bool>;  final _guardadoLocalController = BehaviorSubject<bool>;  Stream<int> get idStream => _idController.stream;  Stream<String> get nombreStream => _nombreController.stream;  Stream<int> get estadoStream => _estadoController.stream;  Stream<int> get idStream => _idController.stream;  Stream<int> get idRemotoStream => _idRemotoController.stream;  Stream<bool> get imagenesSyncStream => _imagenesSyncController.stream;  Stream<bool> get guardadoLocalStream => _guardadoLocalController.stream;  Stream<dynamic> get formValidStream => Observable.combineLatest([    nombreStream,    estadoStream,    idStream,    idRemotoStream,    imagenesSyncStream,    guardadoLocalStream  ], (a) => a);
  Function(MaterialRetiradoModelIR) get reconexionSink => _materialretiradoListController.sink.add;  Function(int) get idSink => _idController.sink.add;  Function(int) get idRemotoSink => _idRemotoController.sink.add;  Function(String) get nombreSink => _nombreController.sink.add;  Function(int) get estadoSink => _estadoController.sink.add;  Function(int) get idSink => _idController.sink.add;  Function(int) get idRemotoSink => _idRemotoController.sink.add;  Function(bool) get imagenesSyncSink => _imagenesSyncController.sink.add;  Function(bool) get guardadoLocalSink => _guardadoLocalController.sink.add;  MaterialRetiradoModelIR get MaterialRetiradoModelIRValue => _materialretiradoListController.value;  int get idValue => _idController.value;  String get nombreValue => _nombreController.value;  int get estadoValue => _estadoController.value;  int get idValue => _idController.value;  int get idRemotoValue => _idRemotoController.value;  bool get imagenesSyncValue => _imagenesSyncController.value;  bool get guardadoLocalValue => _guardadoLocalController.value;  void setModel(MaterialRetiradoModelIR model) {    idSink(model.id);    idRemotoSink(model.idRemoto);    nombreSink(model.nombre);    estadoSink(model.estado);    idSink(model.id);    idRemotoSink(model.idRemoto);    imagenesSyncSink(model.imagenesSync);    guardadoLocalSink(model.guardadoLocal);  }  MaterialRetiradoModelIR getModel() {    return MaterialRetiradoModelIR(      nombre: nombreValue,      estado: estadoValue,      id: idValue,      idRemoto: idRemotoValue,      imagenesSync: imagenesSyncValue,      guardadoLocal: guardadoLocalValue    );  }  dispose() {    _nombreController?.close();    _estadoController?.close();    _idController?.close();    _idRemotoController?.close();    _imagenesSyncController?.close();    _guardadoLocalController?.close();  }}