CREATE TABLE Reconexion ( 
    id  INTEGER PRIMARY KEY,    idRemoto  INTEGER,    imagenesSync BOOLEAN,    fechaGenerado DATE ,    montoPagado INTEGER ,    selloRemovido TEXT ,    selloInstalado TEXT ,    fechaReconexion DATE ,    reconectado BOOLEAN ,    reconectadoEn INTEGER ,    fechaPago DATE ,    causaReconexion INTEGER ,    tipoComentario INTEGER ,    comentarios TEXT ,    nombreCliente TEXT ,    servicio BOOLEAN ,    direccion TEXT ,    cuenta INTEGER ,    ruta INTEGER ,    municipio INTEGER ,    zona TEXT ,    tipoPredio TEXT ,    estadoId INTEGER ,    ciclo INTEGER ,    longitud REAL ,    latitud REAL ,    tipoObservacion INTEGER ,    contador TEXT ,    telefono TEXT ,    celular TEXT ,    identificacion TEXT ,    id BOOLEAN ,    idRemoto BOOLEAN ,    imagenesSync BOOLEAN ,    guardadoLocal BOOLEAN ,  );