CREATE TABLE Persuasion ( 
    id  INTEGER PRIMARY KEY,    idRemoto  INTEGER,    imagenesSync BOOLEAN,    cuenta INTEGER ,    digitoChequeo INTEGER ,    fechaActualizacion DATE ,    nombres TEXT ,    apellidos TEXT ,    identificacion TEXT ,    direccion TEXT ,    telefono TEXT ,    celular TEXT ,    mail TEXT ,    calidad INTEGER ,    ciclo INTEGER ,    fechaVisita DATE ,    saldo REAL ,    periodoVencido INTEGER ,    estado INTEGER ,    fechaGeneracion DATE ,    longitud REAL ,    latitud REAL ,    altitud REAL ,    satelite INTEGER ,    atendidoPor INTEGER ,    codigoAgua TEXT ,    codigoGas TEXT ,    ruta TEXT ,    total TEXT ,    tipoResidencia INTEGER ,    numMinutos TEXT ,    realizado BOOLEAN ,    observacion TEXT ,    id BOOLEAN ,    idRemoto BOOLEAN ,    imagenesSync BOOLEAN ,    guardadoLocal BOOLEAN ,  );